import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
//import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"
import CreateDeckForm from "./CreateDeckForm";



function CreateDeckButton(){
    const history = useHistory(); 
    const[showForm, setShowForm] = useState(false); /*Set form visible set to invisible*/

    function GoHomeButton() {
        const history = useHistory();
        return (
          <button type="button" onClick={() => history.push("/")}>
            Home
          </button>
        );
        }

    function Button(){
        const onChange = (event) => { 
           event.preventDefault();
           console.log("hi")
        }   
       return (
                <button type="submit" value="Submit" onClick={onChange}>Submit</button>
            )
        }

    function Cancel(){
        const onChange = (event) => {
            event.preventDefault();
            history.push("/");
            console.log("bye")
        }    
        return (
            <button type="button" value="Cancel" onClick={onChange}>Cancel</button>
        )
    }

    /*change handle to show form on page*/
    const openForm = () => setShowForm(() => {
        history.push("/decks/new") /*send to new form page*/
        return (  
            <>
            <div>
            <nav><GoHomeButton /></nav>

            <form >
                <table>
                    <tr>
                        <td>
                        <label htmlFor="name">
                        Name
                        <input
                        id="name"
                        type="text"
                        name="name"
                        placeholder="Deck Name"    
                        />   
                        </label>
                        </td>
                    </tr>
                    <tr>
                        <td>
                        <label htmlFor="description">
                        Description
                        <textarea
                         id="description"
                         type="text"
                        name="description"
                        placeholder="Brief description of the deck"   
                        /> 
                        </label>
                        </td>
                    </tr>
                    <tr>
                        <td>
                        <Cancel />
                        </td>
                        <td>
                        <Button />
                        </td>
                    </tr>
                </table>

            </form> 
            </div>
            </>
            )
    } );


    return (
        <>
        <div>
            <button onClick={openForm}>Create Deck</button>
            {showForm}
        </div>
        </>

    )

}

export default CreateDeckButton;
