import { useEffect, useState } from "react";
import CreateDeckForm from "./FillDeckForm";
import {deleteDeck, readDeck, readCard, deleteCard} from "../../utils/api/index"
import { Route, Link, useHistory, useParams } from "react-router-dom";
import SingleDeckView from "./SingleDeckView";

function StudyDeck(){
    const history = useHistory();
    const { deckId } = useParams();
    const [deckView, setDeckView] = useState({});
    const [cardList, setCardList] = useState([]);
    const [cardNumber, setCardNumber] = useState(0);
    const [front, setFront] = useState(false);

    useEffect(() => {
        const abortController = new AbortController();
        async function singleDeck(){
          try{
            const fetch = await readDeck(deckId);
            setDeckView(fetch);
            setCardList(fetch.cards);
          console.log(fetch);
          console.log(fetch.cards);
          }catch(error){
            console.log("Sorry something went wrong", error);
          }
          return () => {
            abortController.abort();
        } 
      };

      singleDeck()

      }, [])

      const changeCard = () => setCardNumber(cardNumber+1);
      const flipCard = () => setFront(!front);

      function handleNext(){
        setCardNumber((currentCard) => currentCard + 1);
        setFront(true);
      }

      function handlePrevious() {
        setCardNumber((currentCard) => currentCard + 1);
        setFront(true);
      }


     function CardFlip(){

        return (
            <>
                <button>Flip</button>
                <button>Next</button>
                <button>Previous</button>
            </>

        )

     } 

     if(cardList.length < 2){
        return (<>
            <div>
              <nav aria-label="breadcrumb">
                        <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                        <Link onClick={() => history.push("/")}>
                          Home
                        </Link>
                        </li>
                        <li className="breadcrumb-item active" aria-current="page">Study</li>
                        </ol>
                    </nav>
              </div>
              <tr>  
                  <h1>{deckView.name} : Study</h1>

                  <div class="container">
                       <h2>Not Enough Cards</h2>
                       <div>
                        <p>You need at least three cards to Study</p>
                        <p>There are {`${cardList.length}`} in this deck</p>
                       </div>
                  </div>


                </tr>
            </>)
     }


    return (
        <>
        <div>
          <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                    <li className="breadcrumb-item">
                    <Link onClick={() => history.push("/")}>
                      Home
                    </Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">Study</li>
                    </ol>
                </nav>
          </div>
          <tr>  
              <h1>Study: {deckView.name}</h1>

              <div class="container">
                    <CardFlip />

              </div>


            </tr>
        </>


        )


}

export default StudyDeck;
