import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"
import Deck from "./Deck";
import FillDeckForm from "./FillDeckForm";




function CreateDeckButton(){
    const history = useHistory(); 
    const[showForm, setShowForm] = useState(false); /*Set form visible set to invisible*/

    function GoHomeButton() {
         return (
          <Link onClick={() => history.push("/")}>
            Home
          </Link>
        );
        }

         const onSubmit = (event) => { 

           console.log("hi")

        }   



         const onCancel = (event) => {

            history.goBack();
            console.log("bye")
        }    


    /*change handle to show form on page*/
    const openForm = () => setShowForm(() => {
        history.push("/decks/new") /*send to new form page*/



        return (  
            <>
            <div>
            <nav aria-label="breadcrumb">
                <ol class="breadcrumb">
                <li class="breadcrumb-item"><Link><GoHomeButton /></Link></li>
                <li class="breadcrumb-item active" aria-current="page">Create Deck</li>
                </ol>
            </nav>

            <h1>Create Deck</h1>

            <FillDeckForm />
            </div>
            </>
            )
    } );


    return (
        <>
            <div className="row">
                <div className="col-sm-6 mb-1">
                    <Link to="/decks/new" className="btn btn-outline-primary" onClick={openForm} ><strong>+ Create Deck</strong></Link>
                </div>
            </div> 
            <div>
                {showForm}
            </div>
        </>

    )

}

export default CreateDeckButton;
