import { useEffect, useState } from "react";
import CreateDeckForm from "./FillDeckForm";
import {deleteDeck, readDeck, readCard, deleteCard} from "../../utils/api/index"
import { Route, Link, useHistory, useParams } from "react-router-dom";
import SingleDeckView from "./SingleDeckView";

function StudyDeck(){
    const history = useHistory();
    const { deckId } = useParams();
    const [deckView, setDeckView] = useState({});
    const [cardList, setCardList] = useState([]);
    const [cardNumber, setCardNumber] = useState(0);
    const [front, setFront] = useState(false);

    const changeCard = () => setCardNumber(cardNumber+1);
    const flipCard = () => setFront(!front);


    useEffect(() => {
        const abortController = new AbortController();
        async function singleDeck(){
          try{
            const fetch = await readDeck(deckId);
            setDeckView(fetch);
            setCardList(fetch.cards);
          console.log(fetch);
          console.log(fetch.cards);
          }catch(error){
            console.log("Sorry something went wrong", error);
          }
          return () => {
            abortController.abort();
        } 
      };

      singleDeck()

      }, [])

      function FlipCurrentCard({currentCard}){
        return (
            <>
            <div onClick={flipCard}>
                <p>{currentCard.name}</p>
            </div>
            </>
        )
      }

    return (
        <>
        <div>
          <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                    <li className="breadcrumb-item">
                    <Link onClick={() => history.push("/")}>
                      Home
                    </Link>
                    </li>
                    <li className="breadcrumb-item active" aria-current="page">Study</li>
                    </ol>
                </nav>
          </div>
          <tr>  
              <h1>Study: {deckView.name}</h1>
              <div >
                {cardList.map((currentCard) => {
                    <FlipCurrentCard key={currentCard.id} currentCard={currentCard}/>
                })

                }

              </div>
            </tr>
        </>

        )


}

export default StudyDeck;
