import DeckList from "../Deck/DeckList";
import React, { useEffect, useState } from "react";
import {Link, useHistory, useRouteMatch, useParams} from "react-router-dom";
import {createCard} from "../../utils/api/index";

function AddCard(){
    const history = useHistory(); 
    /* set initial form properties to blank value */
    const initialFormState = { 
        name: "",
        description: "",
      };
    const [cardformData, setCardFormData] = useState({ ...initialFormState }); /* Set form state to blank object*/

    /* add new formdata to existing data */
    const handleChange = ({ target }) => {
     setformData({
        ...cardformData,
         [target.name]: target.value,
      });
      console.log(target.name);
     };


     async function handleSubmit(event){
        event.preventDefault();  /* prevents page refresh */
        const response = await createCard({...cardformData});
        console.log("Submitted:", cardformData);
        setformData(response);
        setformData({...initialFormState});
        console.log(response);
        history.push("/decks/:deckId/cards"+response.id);
        event.target.reset();

     }

     console.log(cardformData);


     return (

   <>
   <form onSubmit={handleSubmit} >
        <table>
            <tr>
                            <div className="form-group" >
                                <label htmlFor="name" for="exampleFormControlInput1">
                                    Front:
                                    <input
                                    id="exampleFormControlInput1"
                                    type="text"
                                    name="name"
                                    className="container-fluid"
                                    placeholder="Deck Name"
                                    onChange={handleChange}
                                    value={cardformData.name}    
                                    />   
                                </label>
                            </div>    
            </tr>
            <tr>
                            <div className="form-group">
                                <label htmlFor="description" for="exampleFormControlTextarea1">
                                    Back
                                    <textarea
                                    id="exampleFormControlTextarea1"
                                    type="text"
                                    name="description"
                                    className="container-fluid" rows="3"
                                    placeholder="Brief description of the deck"   
                                    onChange={handleChange}
                                    value={cardformData.description}
                                /> 
                                </label>
                            </div>    
            </tr>
            <tr>
                <td>
                        <button type="button" value="Cancel" className="btn btn-secondary btn-lg">Done</button>
                        <button type="submit" value="Submit" className="btn btn-primary btn-lg" to="/decks/:deckId" >Save</button>
                </td>
            </tr>
        </table>
   </form>
   </>
   )

}

export default AddCard;
