import React, { useEffect, useState } from "react";
import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"

function CreateDeckForm(){
    const initialFormState = {
        name: "",
        description: "",
      };
    const [formData, setformData] = useState({ ...initialFormState }); /* Set form state to blank array*/
    const handleChange = ({ target }) => {
        setformData({
          ...formData,
          [target.name]: target.value,
        });
      };


    const handleSubmit = (event) => {
        event.preventDefault();
        console.log("Submitted:", formData);
        setformData({ ...initialFormState });
        event.target.reset();
      };




    return (
        <>
        <div>
        <form onSubmit={handleSubmit}>
              <label htmlFor="name">
              Name
            <input
                id="name"
                type="text"
                name="name"
                placeholder="Deck Name"
                onChange={handleChange}
                value={formData.value}

            />   
            </label>
            <label htmlFor="description">
            Description
            <textarea
                id="description"
                type="text"
                name="description"
                placeholder="Brief description of the deck"
                onChange={handleChange}
                value={formData.value}

            />   

            <button>Cancel</button>
            <button type="submit" value="Submit">Submit</button>

            </label>

        </form>
        </div>
        <div>
        <ul>
        {formData.map((currentValue, index)=> (
            <li key={index}>{currentValue.name}</li>
        ))}
            </ul>
        </div>
        </>
    )
}


export default CreateDeckForm;
