import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck} from "../utils/api/index"


function Home(){
    const [deckListing, setdeckListing] = useState([]);

    useEffect(() => {
      async function fetchList(){
        const findDeck = await listDecks();
        setdeckListing(findDeck);

      }
      fetchList()
    }, [])

    console.log(deckListing);

    const deckMap = deckListing.map((currentDeck, index) => {
        return (<div>
            <li key={index}>{currentDeck.name}</li>
            <li>{`${currentDeck.cards.length}`}</li>
            </div>);
     });

     return (
      <div>
        <Link to="/">Home{deckMap}</Link>
      <li>{`${deckMap.length}`}</li>
        </div>
      )


}

export default Home;
