import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
//import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"
import CreateDeckForm from "./CreateDeckForm";



function CreateDeckButton(){
    const history = useHistory(); 
    const[showForm, setShowForm] = useState(false); /*Set form visible set to invisible*/

    function GoHomeButton() {

        return (
          <Link onClick={() => history.push("/")}>
            Home
          </Link>
        );
        }

    function Button(){
        const onChange = (event) => { 
           event.preventDefault();
           console.log("hi")
        }   
       return (
                <button type="submit" value="Submit" class="btn btn-primary btn-lg" onClick={onChange}>Submit</button>
            )
        }

    function Cancel(){
        const onChange = (event) => {
            event.preventDefault();
            history.push("/");
            console.log("bye")
        }    
        return (
            <button type="button" value="Cancel" class="btn btn-secondary btn-lg" onClick={onChange}>Cancel</button>
        )
    }

    /*change handle to show form on page*/
    const openForm = () => setShowForm(() => {
        history.push("/decks/new") /*send to new form page*/
        return (  
            <>
            <div>
            <nav aria-label="breadcrumb">
                <ol class="breadcrumb">
                <li class="breadcrumb-item"><a href="#"><GoHomeButton /></a></li>
                <li class="breadcrumb-item active" aria-current="page">Create Deck</li>
                </ol>
            </nav>

            <h1>Create Deck</h1>

            <form >
                <table>
                    <tr>
                        <td>
                            <div class="form-group">
                                <label htmlFor="name" for="exampleFormControlInput1">
                                    Name
                                    <input
                                    id="exampleFormControlInput1"
                                    type="text"
                                    name="name"
                                    placeholder="Deck Name"    
                                    />   
                                </label>
                            </div>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <div class="form-group">
                                <label htmlFor="description" for="exampleFormControlTextarea1">
                                    Description
                                    <textarea
                                    id="exampleFormControlTextarea1"
                                    type="text"
                                    name="description"
                                    class="form-control" rows="3"
                                    placeholder="Brief description of the deck"   
                                /> 
                                </label>
                        </div>
                        </td>
                    </tr>
                    <tr>
                        <td>
                        <Cancel />
                        <Button />
                        </td>
                    </tr>
                </table>

            </form> 
            </div>
            </>
            )
    } );


    return (
        <>
        <div>
            <button onClick={openForm}>Create Deck</button>
            {showForm}
        </div>
        </>

    )

}

export default CreateDeckButton;
