import React, { useState } from "react";
import { Route, Link, useHistory, useRouteMatch } from "react-router-dom";
import FillDeckForm from "./FillDeckForm";
import SingleDeckView from "./SingleDeckView";




function CreateDeckButton({singleDeck}){
    const {path, url} = useRouteMatch;
    const history = useHistory(); 
    const[showForm, setShowForm] = useState(false); /*Set form visible set to invisible*/


    function GoHomeButton() {
         return (
          <Link onClick={() => history.push("/")}>
            Home
          </Link>
        );
        }


    /*change handle to show form on page*/
    const openForm = () => setShowForm(() => {
         /*send to new form page*/
        return (  
            <>
            <div>
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                <li className="breadcrumb-item"><Link>
                <Route path={"/"}>
                <GoHomeButton />
                </Route></Link></li>
                <li className="breadcrumb-item active" aria-current="page">Create Deck</li>
                </ol>
            </nav>

            <h1>Create Deck</h1>

            <FillDeckForm/>

            <SingleDeckView />

            </div>
            </>
            )
    } );


    return (


        <>
            <div className="row">
                <div className="col-sm-6 mb-1">
                    <Link to="/decks/new" className="btn btn-outline-primary" onClick={openForm} ><strong>+ Create Deck</strong></Link>
                </div>
            </div> 
            <div>
                {showForm}
            </div>

        </>

    )

}

export default CreateDeckButton;
