import React, { useState } from "react";
import {createDeck} from "../../utils/api/index"
import { useHistory} from "react-router-dom";
import SingleDeckView from "./SingleDeckView";


function CreateDeckForm(){

    const history = useHistory(); 
    /* set initial form properties to blank value */
    const initialFormState = { 
        name: "",
        description: "",
      };
    const [formData, setformData] = useState({ ...initialFormState }); /* Set form state to blank object*/

    /* add new formdata to existing data */
    const handleChange = ({ target }) => {
     setformData({
        ...formData,
         [target.name]: target.value,
      });
      console.log(target.name);
     };

   //   

 /* handle submit event */


   async function handleSubmit(event){
    event.preventDefault();  /* prevents page refresh */
  const response = await createDeck({...formData});
  console.log("Submitted:", formData);
    setformData(response);
    setformData({...initialFormState});
    history.push("/decks/:deckId");
    event.target.reset();

 }



  //const handleSubmit = (event) => {
  //  event.preventDefault();
//  console.log("Submitted:", formData);

   // setformData({...initialFormState});
  //}

    return (
        <>
        <div>
        <form onSubmit={handleSubmit} >
                <table>
                    <tr>
                            <div className="form-group" >
                                <label htmlFor="name" for="exampleFormControlInput1">
                                    Name
                                    <input
                                    id="exampleFormControlInput1"
                                    type="text"
                                    name="name"
                                    className="container-fluid"
                                    placeholder="Deck Name"
                                    onChange={handleChange}
                                    value={formData.name}    
                                    />   
                                </label>
                            </div>    
                    </tr>
                    <tr>
                            <div className="form-group">
                                <label htmlFor="description" for="exampleFormControlTextarea1">
                                    Description
                                    <textarea
                                    id="exampleFormControlTextarea1"
                                    type="text"
                                    name="description"
                                    className="container-fluid" rows="3"
                                    placeholder="Brief description of the deck"   
                                    onChange={handleChange}
                                    value={formData.description}
                                /> 
                                </label>
                            </div>    
                    </tr>
                    <tr>
                        <td>
                        <button type="button" value="Cancel" className="btn btn-secondary btn-lg">Cancel</button>
                        <button type="submit" value="Submit" className="btn btn-primary btn-lg" >Submit</button>

                        </td>
                    </tr>
                </table>


            </form> 


        </div>
        <SingleDeckView />
        </>
    )
}


export default CreateDeckForm;
