import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck} from "../utils/api/index"
//import CreateDeckButton from "./CreateDeck/CreateDeckButton";
//import CreateDeckForm from "./CreateDeck/CreateDeckForm";


function Home({formData, deleteDeck}){
    const [deckListing, setdeckListing] = useState([]); /* Set state to blank array*/

    useEffect(() => {
      async function fetchList(){
        const findDeck = await listDecks(); /* data already fetched from api*/
        setdeckListing(findDeck); /* update blank array of deck to deck data fetched from api*/

      }
      fetchList()
    }, [])

   /* console.log(deckListing);*/

   const deleteButton = (indexToDelete) => 
    setdeckListing((currentDeckValue) => currentDeckValue.filter((deckPost,index) => index !== indexToDelete));


    function DeckMap() {
       return deckListing.map((currentDeck, index) => {
        return (

        <table>
            <thead>
                <tr key={index}>
                    <th>{currentDeck.name}</th>
                    <th>{`${currentDeck.cards.length}`}</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                    {`${currentDeck.description}`}
                    </td>
                </tr>
                <tr>
                    <td>
                        <button>View</button>
                    </td>
                    <td>
                        <button>Study</button>
                    </td>
                    <td>
                        <button name="delete" onClick={deleteButton}>Delete</button>
                    </td>
                </tr>
            </tbody>
        </table>
        );
     });

    }

     return (

        <div>
        <DeckMap deleteButton={() => deleteButton()} />
        </div>

      )



}

export default Home;
