import { useEffect, useState } from "react";
import {useParams} from "react-router-dom";
import CreateDeckForm from "./FillDeckForm";
import {deleteDeck, readDeck, readCard, deleteCard} from "../../utils/api/index"
import { Route, Link, useHistory } from "react-router-dom";


function SingleDeckView(){
  const history = useHistory();
  const { deckId, cardId } = useParams();
  const [deckView, setDeckView] = useState({});
  const [cardList, setCardList] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();
    async function singleDeck(){
      try{
        const fetch = await readDeck(deckId);
        setDeckView(fetch);
        setCardList(fetch.cards);
      console.log(fetch);
      console.log(fetch.cards);
      history.push("/decks/"+fetch.id);
      }catch(error){
        console.log("Sorry something went wrong", error);
      }
      return () => {
        abortController.abort();
    } 
  };

  singleDeck()

  }, [])



  function CardListMap(){ 

    return (


      cardList.map((currentCard, index) => {

      async function deleteHandler(){
        if(window.confirm(`Delete this card? You will not be able to recover it`)){
            history.go(0);
                    return await deleteCard(currentCard.id);
        }
       }

            return (

                <>
                  <div class="row">
                    <div class="col-sm-6">
                      <div class="card">
                      <div class="card-body">
                      <div>
                    <li key={index} style={{listStyle: "none"}} >
                          <div>
                          <strong>Front:</strong> {currentCard.name} 
                          </div> 
                    </li>
                    </div>  
                      <div className="card-text">
                        <div>
                        <strong>Back:</strong> {`${currentCard.description}`}
                        </div>

                      </div>
                      <div class="d-flex justify-content-end"> 
                          <Link  className="btn btn-secondary" onClick={() => history.push(`/decks/${deckView.id}/cards/${currentCard.id}/edit`)}>Edit</Link>
                          <button onClick={deleteHandler} className="btn btn-danger">Delete</button>
                          </div>
                          </div>   
                    </div>
                    </div>        
                  </div>
              </>
          )     
     } )
    )}








    return(


      <>
      <div>
      <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                <li className="breadcrumb-item">
                <Link onClick={() => history.push("/")}>
                  Home
                </Link>
                </li>
                <li className="breadcrumb-item active" aria-current="page">{deckView.name}</li>
                </ol>
            </nav>
      </div>

      <div class="row">
        <div class="col-sm-12 p-0">
          <div class="card">
            <div class="card-body">
              <div>
                <h1>{deckView.name}</h1>
              </div>
              <div>
                <p>{deckView.description}</p>
              </div>
              <div class="row">
              <div class="d-flex justify-content-start">
                  <button onClick={() => history.push(`/decks/${deckView.id}/edit`)} className="btn btn-secondary">Edit</button>

                  <button className="btn btn-primary" onClick={() => history.push(`/decks/${deckView.id}/study`)}>Study</button>

                  <button type="button" onClick={() => history.push(`/decks/${deckView.id}/cards/new`)} className="btn btn-primary">Add Cards</button>
              </div>
              <div class="d-flex justify-content-between">
                  <button className="btn btn-danger" onClick={() => {
                  history.go(0);
                  deleteDeck(deckView.id)}}>Delete</button>
              </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <h1>Cards</h1>
      <CardListMap />
      </>


)


}

export default SingleDeckView;
