import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import {listDecks, deleteDeck} from "../utils/api/index"
import CreateDeckButton from "./CreateDeck/CreateDeckButton";

//import CreateDeckForm from "./CreateDeck/CreateDeckForm";


function Home(){
    const history = useHistory();
    const [deckListing, setdeckList] = useState([]); /* Set state to blank array*/

    useEffect(() => {
      async function fetchList(){
        const findDeck = await listDecks(); /* data already fetched from api*/
        setdeckList(deckListing); /* update blank array of deck to deck data fetched from api*/
      }
      fetchList()
    }, [])

    console.log(deckListing);

    function DeckMap(){
        return (deckListing.map((currentDeck, index) => {
                  return (
                      <div>
                      <>
                      <table>
                          <thead>
                                  <tr key={index}>
                                      <th key={currentDeck.id}>{currentDeck.name}</th>
                                      <th key={currentDeck.cards.id}>{`${currentDeck.cards.length}`}</th>
                                  </tr>
                          </thead>
                          <tbody>
                              <tr>
                                  <td>
                                  {`${currentDeck.description}`}
                                  </td>
                              </tr>
                              <tr>
                                  <td>
                                      <Link to={`/decks/${currentDeck.id}`}> View</Link>
                                  </td>
                                  <td>
                                      <Link to={`/decks/${currentDeck.id}/study`}>Study</Link>
                                  </td>
                                  <td>
                                      <button >Delete</button>

                                  </td>
                              </tr>
                          </tbody>
                      </table>
                      </>
                  </div>

                )

         })
         )}



     return (

        <div>
        <CreateDeckButton />
        </div>

      )



}

export default Home;
