import React, { useEffect, useState } from "react";

function CreateDeckForm(){
    const [formData, setformData] = useState([]); /* Set form state to blank array*/
    const onChange = (event) => setformData(event.target.value);

    const createNewDeckForm = (newValue) => {
        setformData((currentValue) => [...currentValue, newValue]) ;
        console.log(formData);
    }

    const submitHandler = (event) => {
        event.preventDefault();
        createNewDeckForm(formData);
        setformData("");
        event.target.reset();
    }

    return (
        <form onSubmit={submitHandler}>
              <label htmlFor="name">
              Name
            <input
                id="name"
                type="text"
                name="name"
                placeholder="Deck Name"
                onChange={onChange}
                value={formData.value}

            />   
            </label>
            <label htmlFor="description">
            Description
            <textarea
                id="description"
                type="text"
                name="name"
                placeholder="Brief description of the deck"
                onChange={onChange}
                value={formData.value}

            />   

            <button>Cancel</button>
            <button type="submit" value="Submit">Submit</button>
            </label>

        </form>
    )
}


export default CreateDeckForm;
