import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck} from "../utils/api/index"


function Home(){
    const [deckListing, setdeckListing] = useState([]); /* Set state to blank array*/

    useEffect(() => {
      async function fetchList(){
        const findDeck = await listDecks(); /* data already fetched from api*/
        setdeckListing(findDeck); /* update blank array of deck to deck data fetched from api*/

      }
      fetchList()
    }, [])

   /* console.log(deckListing);*/

    const deckMap = deckListing.map((currentDeck, index) => {
        return (<div>
            <li key={index}><strong>{currentDeck.name} {`${currentDeck.cards.length}`}</strong></li> {/* Deck Name*/}
            <br />
            <p>{currentDeck.description}</p>
            <p></p> {/* Deck Cards numbers*/}
            </div>);
     });

     return (
      <div>
        <p>{deckMap}</p>
        </div>
      )


}

export default Home;
