import React, { useEffect, useState } from "react";
import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"
import {useHistory} from "react-router-dom";

function CreateDeckForm(){
    const history = useHistory(); 
    /* set initial form properties to blank value */
    const initialFormState = { 
        name: "",
        description: "",
      };
    const [formData, setformData] = useState({ ...initialFormState }); /* Set form state to blank object*/

    /* add new formdata to existing data */
    const handleChange = ({ target }) => {
        setformData({
          ...formData,
          [target.name]: target.value,
        });
      };

 /* handle submit event */
 useEffect(() => {
    async function handleSubmit(event){
        event.preventDefault();  /* prevents page refresh */
        try{
            const abortController = new AbortController();
            const response = await createDeck({ ...formData }, {signal: abortController.signal})
            history.push("/");
            setformData(response);
        }catch(error){
            if(error.name === "AbortError"){
                return "Aborted";
            }
            else{
                throw error;
            }
        }


        return response;

      };
 })


    return (
        <>
        <div>
        <form onSubmit={handleSubmit}>
              <label htmlFor="name">
              Name
            <input
                id="name"
                type="text"
                name="name"
                placeholder="Deck Name"
                onChange={handleChange}
                value={formData.value}

            />   
            </label>
            <label htmlFor="description">
            Description
            <textarea
                id="description"
                type="text"
                name="description"
                placeholder="Brief description of the deck"
                onChange={handleChange}
                value={formData.value}

            />   

            <button>Cancel</button>
            <button type="submit" value="Submit">Submit</button>

            </label>

        </form>
        </div>
        </>
    )
}


export default CreateDeckForm;
