import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck, deleteDeck} from "../utils/api/index"
//import CreateDeckButton from "./CreateDeck/CreateDeckButton";
//import CreateDeckForm from "./CreateDeck/CreateDeckForm";


function Home({formData, deleteDeck}){
    const history = useHistory();
    const [deckListing, setdeckListing] = useState([]); /* Set state to blank array*/

    useEffect(() => {
      async function fetchList(){
        const findDeck = await listDecks(); /* data already fetched from api*/
        setdeckListing(findDeck); /* update blank array of deck to deck data fetched from api*/

      }
      fetchList()
    }, [])

   /* console.log(deckListing);*/

   async function handleDelete(deck) {
    if (
        window.confirm(
            `Delete this deck? You will not be able to recover it`
        )
    )
        history.go(0);
        return await deleteDeck(deck.id);
    }

 function DeckMap() {
       return deckListing.map((currentDeck, index) => {
        return (

        <table>
            <thead>
                <tr key={index}>
                    <th>{currentDeck.name}</th>
                    <th>{`${currentDeck.cards.length}`}</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                    {`${currentDeck.description}`}
                    </td>
                </tr>
                <tr>
                    <td>
                        <Link to="/decks/{}">View</Link>
                    </td>
                    <td>
                        <Link>Study</Link>
                    </td>
                    <td>
                        <button onClick={() => handleDelete(currentDeck)}>Delete</button>
                    </td>
                </tr>
            </tbody>
        </table>
        );
     });

    }

     return (

        <div>
        <DeckMap />
        </div>

      )



}

export default Home;
