import { useEffect, useState } from "react";
import SingleDeckView from "./SingleDeckView";
import {deleteDeck, readDeck, readCard, deleteCard, updateDeck, updateCard} from "../../utils/api/index"
import { Route, Link, useHistory, useParams } from "react-router-dom";


function EditCard(){
    const {cardId} = useParams();
    console.log(cardId);
    const history = useHistory();

    const initialDeckState = {
        name: "",
        description: "",
    };
    const [cardEdit, setCardEdit] = useState(initialDeckState);

    function handleChange({ target }) {
        setCardEdit({
            ...cardEdit,
            [target.name]: target.value,
        });
    }

    useEffect(() => {
        const abortController = new AbortController();
        async function editCard(){
          try{
            const fetch = await readCard(cardId);
            setCardEdit(fetch);
          console.log(fetch);
          }catch(error){
            console.log("Sorry something went wrong", error);
          }
          return () => {
            abortController.abort();
        } 
      };

      editCard()

      }, [])



      async function handleSubmit(event) {
        event.preventDefault();
        const response = await updateCard({...cardEdit});
        console.log(response);
        setDeckEdit({...response});
    }
    console.log(deckEdit);
    return (
        <>
       <p>Edit Deck</p>

        <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                <li className="breadcrumb-item">
                <Link onClick={() => history.push("/")}>
                  Home
                </Link></li>
                <li className="breadcrumb-item active" aria-current="page">Edit Deck</li>
                </ol>
            </nav>
        <div>
            <h1>Edit Deck</h1>
        <form onSubmit={handleSubmit} >
                <table>
                    <tr>
                            <div className="form-group" >
                                <label htmlFor="name" for="exampleFormControlInput1">
                                    Name
                                    <input
                                    id="exampleFormControlInput1"
                                    type="text"
                                    name="name"
                                    onChange={handleChange}
                                    className="container-fluid"
                                   value={deckEdit.name}    
                                    />   
                                </label>
                            </div>    
                    </tr>
                    <tr>
                            <div className="form-group">
                                <label htmlFor="description" for="exampleFormControlTextarea1">
                                    Description
                                    <textarea
                                    id="exampleFormControlTextarea1"
                                    type="text"
                                    name="description"
                                    onChange={handleChange}
                                    className="container-fluid" rows="3"
                                    value={deckEdit.description}
                                /> 
                                </label>
                            </div>    
                    </tr>
                    <tr>
                        <td>
                        <button type="button" value="Cancel" className="btn btn-secondary btn-lg" onClick={() => history.push("/")} >Cancel</button>
                        <button type="submit" value="Submit" className="btn btn-primary btn-lg" >Submit</button>

                        </td>
                    </tr>
                </table>
            </form>            
        </div>

        </>

    )
}

export default EditCard;
