import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import {listDecks, deleteDeck} from "../utils/api/index"
import CreateDeckButton from "./CreateDeck/CreateDeckButton";

//import CreateDeckForm from "./CreateDeck/CreateDeckForm";


function Home({formData, deleteDeck}){
    const history = useHistory();
    const [deckList, setdeckList] = useState([]); /* Set state to blank array*/

    useEffect(() => {
      async function fetchList(){
        const findDeck = await listDecks(); /* data already fetched from api*/
        setdeckListing(findDeck); /* update blank array of deck to deck data fetched from api*/

      }
      fetchList()
    }, [])

   /* console.log(deckListing);*/



     return (

        <div>
        <CreateDeckButton />
        {deckList.map((currentDeck, index) => {
        return (
            <>
            <table>
                <thead>
                        <tr key={index}>
                            <th key={currentDeck.id}>{currentDeck.name}</th>
                            <th key={currentDeck.cards.id}>{`${currentDeck.cards.length}`}</th>
                        </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>
                        {`${currentDeck.description}`}
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <Link to={`/decks/${currentDeck.id}`}> View</Link>
                        </td>
                        <td>
                            <Link to={`/decks/${currentDeck.id}/study`}>Study</Link>
                        </td>
                        <td>
                            <button>Delete</button>

                        </td>
                    </tr>
                </tbody>
            </table>
            </>
        );
     })}
        </div>

      )



}

export default Home;
