import React, { useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { createCard } from "../../utils/api/index";
import SingleDeckView from "../Deck/SingleDeckView";

function AddCard({deckView}){
    const history = useHistory();
    const { deckId } = useParams(); 
    /* set initial form properties to blank value */
    const initialFormState = { 
        name: "",
        description: "",
      };
    const [cardformData, setCardFormData] = useState({ ...initialFormState }); /* Set form state to blank object*/ 

    /* add new formdata to existing data */
    const handleChange = ({ target }) => {
        setCardFormData({
        ...cardformData,
         [target.name]: target.value,
      });

     };


     async function handleSubmit(event){
        event.preventDefault();  /* prevents page refresh */
        const response = await createCard(deckId,cardformData);
        console.log("Submitted:", cardformData);
        setCardFormData(response);
        setCardFormData({...initialFormState});
        console.log(response);     
        event.target.reset();
     }


     console.log(cardformData);


     return (

   <>
   <div>
      <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                <li className="breadcrumb-item">
                <Link onClick={() => history.push("/")}>
                  Home
                </Link>
                </li>
                <li className="breadcrumb-item active" aria-current="page">{deckView.name}</li>
                </ol>
            </nav>
      </div>
   <form onSubmit={handleSubmit}  >

        <table>
            <tr>
                            <div className="form-group" >
                                <label htmlFor="name" for="exampleFormControlInput1">
                                    Front:
                                    <input
                                    id="exampleFormControlInput1"
                                    type="text"
                                    name="name"
                                    className="container-fluid"
                                    placeholder="Front side of card"
                                    onChange={handleChange}
                                    value={cardformData.name}    
                                    />   
                                </label>
                            </div>    
            </tr>
            <tr>
                            <div className="form-group">
                                <label htmlFor="description" for="exampleFormControlTextarea1">
                                    Back
                                    <textarea
                                    id="exampleFormControlTextarea1"
                                    type="text"
                                    name="description"
                                    className="container-fluid" rows="3"
                                    placeholder="Back side of card"   
                                    onChange={handleChange}
                                    value={cardformData.description}
                                /> 
                                </label>
                            </div>    
            </tr>
            <tr>
                <td>
                        <button type="button" value="Cancel" className="btn btn-secondary btn-lg" onClick={() => history.push(`/decks/${deckId}`)}>Done</button>
                        <button type="submit" value="Submit" className="btn btn-primary btn-lg" >Save</button>
                </td>
            </tr>
        </table>
   </form>
   </>
   )

}

export default AddCard;
