import SingleDeckView from "../Deck/SingleDeckView";
import { useEffect, useState } from "react";
import {useParams} from "react-router-dom";
import CreateDeckForm from "./FillDeckForm";
import {deleteDeck, readDeck, readCard, deleteCard} from "../../utils/api/index"
import { Route, Link, useHistory } from "react-router-dom";

function CardListMap({deckView, cardList}){
    const history = useHistory();
    const { deckId, cardId } = useParams();

    return (cardList.map((currentCard, index) => {

            return (

                <>
                  <div>

                  <table class="table">         
                                    <li key={index} style={{listStyle: "none"}} >
                                    <thead>
                                        <td>
                                        <h1> {currentCard.name} 
                                        </h1>
                                        </td> 

                                        </thead>
                                  </li>
                      <tbody>   
                          <tr>
                              <td className="card-text">
                              {`${currentCard.description}`}
                              </td>
                          </tr>
                          </tbody>
                    </table> 
                    <table class="table">

                          <tr>

                              <td>
                                  <Link  className="btn btn-secondary">Edit</Link>


                              </td>

                              <td>
                                  <button onClick={() => {
                                    history.go(0);
                                   return deleteDeck(deleteCard.id);

                                }} className="btn btn-danger">Delete</button>
                              </td>


                          </tr> 
                          </table>
              </div>
          </>
          )     
     } )
     )}

export default CardListMap;
