import { useEffect, useState } from "react";
import {useParams} from "react-router-dom";
import CreateDeckForm from "./FillDeckForm";
import {deleteDeck, readDeck, readCard} from "../../utils/api/index"
import { Route, Link, useHistory } from "react-router-dom";


function SingleDeckView(){
  const history = useHistory();
  const { deckId, cardId } = useParams();
  const [deckView, setDeckView] = useState({});
  const [cardList, setCardList] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();
    async function singleDeck(){
      try{
        const fetch = await readDeck(deckId);
        setDeckView(fetch);
      console.log(fetch);
      console.log(fetch.cards);
      history.push("/decks/"+fetch.id);
      }catch(error){
        console.log("Sorry something went wrong", error);
      }
      return () => {
        abortController.abort();
    } 
  };

  singleDeck()

  }, [])


  useEffect(() => {
    const abortController = new AbortController();
    async function cardList(){
      try{
        const fetch = await readCard(cardId);
        setCardList(fetch);
      console.log(fetch);


      }catch(error){
        console.log("Sorry something went wrong", error);
      }
      return () => {
        abortController.abort();
    } 
  };

  cardList()

  }, [])

  function CardListMap(){

    return (cardList.map((currentDeck, index) => {

            return (

                <>
                  <div>

                  <table class="table">         
                                    <li key={index} style={{listStyle: "none"}} >
                                    <thead>
                                        <td>
                                        <h1> {currentDeck.name} 
                                        </h1>
                                        </td> 

                                        <td >
                                        {`${currentDeck.cards.length}`} cards
                                        </td>                                           
                                        </thead>
                                  </li>
                      <tbody>   
                          <tr>
                              <td className="card-text">
                              {`${currentDeck.description}`}
                              </td>
                          </tr>
                          </tbody>
                    </table> 
                    <table class="table">

                          <tr>

                              <td>
                                  <Link to={`/decks/${currentDeck.id}`} className="btn btn-secondary">View</Link>

                                  <Link to={`/decks/${currentDeck.id}/study`} type="button" className="btn btn-primary">Study</Link>
                              </td>

                              <td>
                                  <button onClick={() => {
                                    history.go(0);
                                   return deleteDeck(currentDeck.id);

                                }} className="btn btn-danger">Delete</button>
                              </td>


                          </tr> 
                          </table>
              </div>
          </>
          )     
     } )
     )}


   if(cardList.length > 0){
    return(
      <>
      <div>
      <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                <li className="breadcrumb-item">
                <Link onClick={() => history.push("/")}>
                  Home
                </Link>
                </li>
                <li className="breadcrumb-item active" aria-current="page">{deckView.name}</li>
                </ol>
            </nav>
      </div>
      <table class="table">


        <tr>  
          <h1>{deckView.name}</h1>
        </tr>
        <tr>
          <p>{deckView.description}</p>
        </tr>
        </table>
        <table class="table">
        <tr>

          <td>
            <button onClick={() => history.push(`/decks/${deckView.id}/edit`)} className="btn btn-secondary">Edit</button>

            <button className="btn btn-primary">Study</button>

            <button type="button" onClick={() => history.push(`/decks/${deckView.id}/cards/new`)} className="btn btn-primary">Add Cards</button>
            </td>
            <td>
            <button className="btn btn-danger" onClick={() => {
            history.go(0);
            deleteDeck(deckView.id)}}>Delete</button>
          </td>
      </tr>
      </table>
      </>

    )

   }else{
    return "No Deck found";
   }




}

export default SingleDeckView;
