import {listDecks, createDeck, readDeck, updateDeck} from "../utils/api/index"

function CreateDeckForm(){
    const initialFormState = {
        name: "",
        email: "",
      };
    const [formData, setformData] = useState({ ...initialFormState }); /* Set form state to blank array*/
    const onChange = (event) => setformData(event.target.value);

    const createNewDeckForm = (newValue) => {
        setformData((currentValue) => [...currentValue, newValue]) ;
        console.log(formData);
    }



    useEffect(() => {
        async function submitHandler(event){
            event.preventDefault();
          const createNewDeck = await createDeck(); /* data already fetched from api*/
          setformData(createNewDeck); /* update blank array of deck to deck data fetched from api*/
          setformData("");
          event.target.reset();
        }
        fetchList()
      }, [])
    return (
        <form onSubmit={submitHandler}>
              <label htmlFor="name">
              Name
            <input
                id="name"
                type="text"
                name="name"
                placeholder="Deck Name"
                onChange={onChange}
                value={formData.value}

            />   
            </label>
            <label htmlFor="description">
            Description
            <textarea
                id="description"
                type="text"
                name="name"
                placeholder="Brief description of the deck"
                onChange={onChange}
                value={formData.value}

            />   

            <button>Cancel</button>
            <button type="submit" value="Submit">Submit</button>
            </label>

        </form>
    )
}


export default CreateDeckForm;
