import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"



function CreateDeckButton({formData, createNewDeckForm}){

    const[example, setExample] = useState(["Hi", "Bye"]);

    const[showForm, setShowForm] = useState(false);

    const openForm = () => setShowForm(() => {
        return (
            <>
            <div>
            <form o>
                  <label htmlFor="name">
                  Name
                <input
                    id="name"
                    type="text"
                    name="name"
                    placeholder="Deck Name"


                />   
                </label>
                <label htmlFor="description">
                Description
                <textarea
                    id="description"
                    type="text"
                    name="description"
                    placeholder="Brief description of the deck"

                />   

                <button>Cancel</button>
                <button type="submit" value="Submit">Submit</button>

                </label>

            </form>
            </div>
            </>
        )
    } );


    const onChange = () => setExample([...example, "Fi"]);

    return (
        <>
        <div>

            <button onClick={onChange}>Create Deck</button>
            {example.map((currentE, id) => <li index={id}>{currentE}</li>)}

        </div>
        </>

    )

}

export default CreateDeckButton;
