import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"



function CreateDeckButton({formData, createNewDeckForm}){
    const history = useHistory(); 

    const[showForm, setShowForm] = useState(false); /*Set form visible set to invisible*/

    /*change handle to show form on page*/
    const openForm = () => setShowForm(() => {
        history.push("/decks/new")
        return (

            <>
            <div>
            <form o>
                  <label htmlFor="name">
                  Name
                <input
                    id="name"
                    type="text"
                    name="name"
                    placeholder="Deck Name"


                />   
                </label>
                <label htmlFor="description">
                Description
                <textarea
                    id="description"
                    type="text"
                    name="description"
                    placeholder="Brief description of the deck"

                />   

                <button>Cancel</button>
                <button type="submit" value="Submit">Submit</button>

                </label>

            </form>
            </div>
            </>
            )
    } );


    return (
        <>
        <div>
            <button onClick={openForm}>Create Deck</button>
            {showForm}
        </div>
        </>

    )

}

export default CreateDeckButton;
