import React, { useEffect, useState } from "react";
import {listDecks, createDeck, readDeck, updateDeck, deleteDeck} from "../../utils/api/index"
import {useHistory, Link} from "react-router-dom";
import CardList from "../Card/CardList";

function Deck(){
    const history = useHistory();
    const [deckListing, setDeckListing] = useState([]);
    const [cardList, setCardList] = useState([]);
    useEffect(() => {
        async function deckDashBoard(){
                const fetch = await listDecks();
                setDeckListing(fetch);  
            }
        deckDashBoard();
        }, [])

    console.log(deckListing.cards); 

    useEffect(() => {
        async function cardRetrieve(){
            const fetchCards = await readDeck();
            setCardList(fetchCards);
        }
    })

    //const deleteDeck = (indexToDelete) => {
      //  setDeckListing((currentDeck) => currentDeck.filter((post, id) => id !== indexToDelete))
   // }

    function DeckMap(){

        return (deckListing.map((currentDeck, index) => {
                return (

                    <>
                      <div>

                      <table>
                          <thead>
                                  <tr>
                                      <th className="card-title">
                                        <li key={index}>{currentDeck.name} 
                                      {`${currentDeck.cards.length}`}
                                      </li>
                                      </th>
                                  </tr>
                          </thead>
                          <tbody>   
                              <tr>
                                  <td className="card-text">
                                  {`${currentDeck.description}`}
                                  </td>
                              </tr>
                              </tbody>
                        </table>      
                              <tr>
                                  <td>
                                      <Link to={`/decks/${currentDeck.id}`}><CardList /></Link>

                                      <Link to={`/decks/${currentDeck.id}/study`} type="button" className="btn btn-primary">Study</Link>
                                  </td>
                                  <td>
                                      <button onClick={() => {
                                       return deleteDeck(currentDeck.id);
                                       setDeckListing([...deckListing]);
                                    }} className="btn btn-danger">Delete</button>
                                  </td>
                              </tr>      
                  </div>
              </>
              )     
         } )
         )}


    return (
                <DeckMap />
    )
}


export default Deck;
