import React, { useEffect, useState } from "react";
import {listDecks, createDeck, readDeck, updateDeck} from "../../utils/api/index"
import {useHistory, Link} from "react-router-dom";

function Deck(){

    const [deckListing, setDeckListing] = useState([]);
    useEffect(() => {
        async function deckDashBoard(){
                const fetch = await listDecks();
                setDeckListing(fetch);  
            }
        deckDashBoard();
        }, [])

    console.log(deckListing); 


    function Delete(){

        return (
                <button>Delete</button>
        )
    }


    function DeckMap(){
        return (deckListing.map((currentDeck, index) => {
                return (

                    <>
                      <div>

                      <table>
                          <thead>
                                  <tr key={index}>
                                      <th key={currentDeck.id} class="card-title">{currentDeck.name}</th>
                                      <th key={currentDeck.cards.id}>{`${currentDeck.cards.length}`}</th>
                                  </tr>
                          </thead>
                          <tbody>   
                              <tr>
                                  <td class="card-text">
                                  {`${currentDeck.description}`}
                                  </td>
                              </tr>
                              </tbody>
                        </table>      
                              <tr>
                                  <td>
                                      <Link to={`/decks/${currentDeck.id}`} type="button" class="btn btn-secondary"> View</Link>

                                      <Link to={`/decks/${currentDeck.id}/study`} type="button" class="btn btn-primary">Study</Link>
                                  </td>
                                  <td>
                                      <Delete />

                                  </td>
                              </tr>      
                  </div>
              </>
              )     
         } )
         )}


    return (
                <DeckMap />
    )
}


export default Deck;
